package br.com.senaisp.aula02;

public class ExercicioAula02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Primeira expressão
		double dblConta = 2.00 / 5.00;
		System.out.println("Resultado da primeira expressão é:"+ dblConta);
		//Segunda expressão
		double dblconta1 = 2.00 / 5.00 + 10;
		System.out.println("Resultado da segunda expressão é:" + dblconta1);
		//Terceira expressão
		double dblconta2 = 1.00 / 5 * 3.00 / 4 + 5;
		System.out.println("Resultado da terceira expressão é:" + dblconta2);
		//Quarta expressão 
		double dblconta3 = 1.00 / 5 * 3.00 / 4 + 5;
		System.out.println("Resultado da quarta expressão é" + dblconta3);

	}

}
