package br.com.senaisp.aula07;

public class Aula07Exe01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Exemplo de laços de repetições de que ao invés de ser com while é com FOR
		for (int intvalor = 1; intvalor < 100; intvalor++) {
			if (intvalor % 2 == 0)
				System.out.println("O número" + intvalor + "é par");
			else 
				System.out.println("O número " + intvalor + "é impar");
			
			
			
		}
		
		
		
		
		
		
		
		

	}

}
